vector preOrder(TreeNode* root){
    vector res;
    if(!root) return res;
    stack s;
    s.push(root);
    while(!s.empty()){
        TreeNode* t = s.top();
        s.pop();
        res.push_back(t->val);
        if(t->right) s.push(t->right);
        if(t->left) s.push(t->left);
    }
    return res;
}

vector inOrder(TreeNode* root){
    vector res;
    if(!root) return res;
    stack s;
    TreeNode* p = root;
    while(!s.empty() || p){
        while(p){
            s.push(p);
            p = p->left;
        }
        TreeNode* t = s.top();
        s.pop();
        res.push_back(t->val);
        p = t->right;
    }
    return res;
}

