 * };
 */
class Solution {
public:
    // 递归获取最大直径，并返回该节点的深度，方便上一层使用
    int diameter(TreeNode* root, int &res){
        if(!root)
            return 0;
        int l = diameter(root->left, res);
        int r = diameter(root->right, res);
        res = max(res, l+r);
        return max(l, r)+1;
    }
    int diameterOfBinaryTree(TreeNode* root) {
        int res = 0;
        diameter(root, res);
        return res;
    }
};

